import React from 'react';
import Nav from './Nav';
import Students from "./components/Students"
// import Rahima from './components/Rahima';
// import Sandra from './components/Sandra';
// import Wietske from './components/Wietske';
// import Storm from './components/Storm';
import AllData from './AllData'
import {BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import './App.css';

function App() {
  // const [state, setState] = useState(AllData)

  const names = AllData.map((item) => item.name);
  console.log("alle-namen: ", names);

  const getNames = [...new Set(AllData.map((item) => item.name))];
  console.log("Student-niet-dubbel: ", getNames);
  const linkItems = getNames.map((person) => (
    <li key={person}>
      <Link to={`/${person}`}>{person}</Link>
    </li>
  ))

  const routeItems = getNames.map((person) => (
    <Route path={`/${person}`} key={person}>
      <Students
        person={person}
        // getDataOfRightStudent={getDataOfRightStudent}
      />
    </Route>
  ))
  const getAssignment = [...new Set(AllData.map((item) => item.assignment))];
  console.log("getAssignment: ", getAssignment);

  const assignmentList = AllData.map((item) => item.assignment);
  console.log("assignmentList: ", assignmentList);

  const getDifficulty = [...new Set(AllData.map((item) => item.difficulty))];
  console.log("getDifficulty: ", getDifficulty);

  const getEnjoyment = [...new Set(AllData.map((item) => item.enjoyment))];
  console.log("getDifficulty: ", getEnjoyment);

  return (

    <Router>
      <div>
        <ul className="nav-links">
          {linkItems}
        </ul>
        {/* <Nav /> */}
        <Switch>
          <ul>
          {routeItems}
          </ul>

          {/* <Route path="/" exact component={Home}/>
          <Route path="/Rahima" component={Rahima}/>
          <Route path="/Sandra" component={Sandra}/>
          <Route path="/Wietske" component={Wietske}/>
          <Route path="/Storm" component={Storm}/> */}
        </Switch>
      </div>
    </Router>
  );
}


const Home = () => (
  <div>
    <h1>students</h1>
  </div>
);


export default App;
