import React from 'react';
import Students from "./components/Students"
import Home from "./components/Home"
import AllData from './AllData'
import {BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
// import Victory from './victory/Victory';
import './App.css';

function App() {

  const sortAllNames = AllData.map((item) => item.name);
  console.log("alle-namen: ", sortAllNames);

  const sortAllAssignments = AllData.map((item) => item.assignment);
  console.log("alle-assignments: ", sortAllAssignments);

  const removeDoubleNames = [...new Set(AllData.map((item) => item.name))];
  console.log("Student-niet-dubbel: ", removeDoubleNames);


  const averageDifficulty = (assignment) => {
    const getTotalDifficultyRating = AllData
          .filter((item) => {
             return item.assignment === assignment;
          })
          .map((item) => item.difficulty)
          .reduce((currentTotal, grading) => {
             return grading + currentTotal;
          }, 0);
       return getTotalDifficultyRating / removeDoubleNames.length;
    };
  console.log("gemiddelde van W1D1-1", averageDifficulty("W1D1-1"));
  console.log("gemiddelde van W4D2-1", averageDifficulty("W4D2-1"));
  console.log("gemiddelde van W6D1-1", averageDifficulty("W6D1-1"));

  const assignmentRatingAverageWithLabels = sortAllAssignments.map((item) => ({
    assignment: item,
    difficultyRating: averageDifficulty(item),
    // enjoymentRating: getAverageEnjoymentRating(item),
    label: `Difficulty Rating: ${averageDifficulty(item).toFixed(
       1
    )} \n Enjoyment Rating: ${getAverageEnjoymentRating(item).toFixed(1)}`,
 }));

 console.log("assignmentRatingAverageWithLabels: ", assignmentRatingAverageWithLabels);
 console.log(assignmentRatingAverageWithLabels.map((averages) => averages.assignment));

  const createlinks = removeDoubleNames.map((name) => (
    <li key={name}>
      <Link to={`/${name}`}>{name}</Link>
    </li>
  ))

  const createRoute = removeDoubleNames.map((name) => (
    <Route path={`/${name}`} key={name}>
      <Students
        studentName={name}
      />
    </Route>
  ))

  const sortDifficulty = [...new Set(AllData.map((item) => item.difficulty))];
  console.log("sortDifficulty: ", sortDifficulty);

  const sortEnjoyment = [...new Set(AllData.map((item) => item.enjoyment))];
  console.log("sortDifficulty: ", sortEnjoyment);

  return (

    <Router>
      <div>
        <ul className="navLinks">
          <Link to="/">Home</Link>
          {createlinks}
        </ul>
        <Switch>
          <Route path="/" exact component={Home} />
          {createRoute}
        </Switch>
      </div>
      <div>
      {/* <Victory /> */}
      </div>
    </Router>

  );
}



export default App;
