import React from 'react';
import Nav from './Nav';
// import Rahima from './components/Rahima';
import Sandra from './components/Sandra';
import Wietske from './components/Wietske';
import Storm from './components/Storm';
import AllData from './AllData'
import {BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import './App.css';

class App extends React.Component{
  constructor() {
    super()
    this.state = {
      Students: AllData
    }
    console.log("studenten", this.state.Students)
  }

  namesOnly() {

    const myItems = this.state.Students

    const newArray = myItems.filter(item => item.name)

    this.setState(prevState => ({
      items: prevState.items = newArray
    }))

    console.log('handle remove runned', myItems, newArray)

  }



render() {
  return (
    <Router>
      <div>
        <Nav />
        <Switch>
          <Route path="/" exact component={Home}/>
          <Route path="/Rahima" component={this.state.Students}/>
          <Route path="/Sandra" component={Sandra}/>
          <Route path="/Wietske" component={Wietske}/>
          <Route path="/Storm" component={Storm}/>
        </Switch>
      </div>
    </Router>
  );
}
}
const Home = () => (
  <div>
    <h1>students</h1>
  </div>
);


export default App;
