import React from 'react';
import AllData from '../AllData'
import { VictoryBar, VictoryChart, VictoryAxis } from 'victory';

 // 3. Function result FUN and Difficulty


// //4. Data with Average result
// const allStudentsRatingAverage = allUniqueAssignments.map((Assignment) => ({
//   Assignment: Assignment,
//   Difficulty: getAverageResult(Assignment, "Difficulty"),
//   Fun: getAverageResult(Assignment, "Fun"),
// }));

console.log(getAverageResult);

const assignmentList = AllData.map((item) => item.assignment);
console.log("assignmentList: ", assignmentList);

const sortAssignment = [...new Set(AllData.map((item) => item.assignment))];
console.log("sortAssignment: ", sortAssignment);

const getAverageResult = (assignment, typeOfResult) => {
  const filterData = AllData
    .filter((item) => sortAssignment === assignment)
    .map((result) => result[typeOfResult]);
  // Average
  const averageResult =
    filterData.reduce((a, b) => a + b) / filterData.length; // ex. const arrAvg = arr => arr.reduce((a,b) => a + b, 0) / arr.length
  return averageResult;
};

// const data = [
//     {quarter: 1, earnings: 13000},
//     {quarter: 2, earnings: 16500},
//     {quarter: 3, earnings: 14250},
//     {quarter: 4, earnings: 19000}
//   ];

  class Victory extends React.Component {
    render() {
      return (
        <VictoryChart domainPadding={10}>
           <VictoryAxis
            fixLabelOverlap
            style={{ tickLabels: { padding: 0, fontSize: 5, angle: 45, } }}
          // tickValues specifies both the number of ticks and where
          // they are placed on the axis
          // tickValues={[1, 2, 3, 4]}
          tickFormat={sortAssignment}

        />
        <VictoryAxis
          dependentAxis
          // tickFormat specifies how ticks should be displayed
          tickFormat={(x) => (`$${x / 1000}k`)}
        />
        <VictoryBar
          data={sortAssignment}
          style={{
            data: { fontSize: 1,}
          }}
          // data accessor for x values
          x="quarter"
          // data accessor for y values
          y="earnings"
        />
        </VictoryChart>
      )
    }
  }
  export default Victory
