import React, { useState } from "react";
import Nav from './Nav';
import Rahima from './components/Rahima';
import Sandra from './components/Sandra';
import Wietske from './components/Wietske';
import Storm from './components/Storm';
import AllData from './AllData';
import {BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import './App.css';


const App = () => {
  const [state, setState] = useState(AllData);

  const getDataOfRightStudent = (student) => {
    return state.filter((item) => item.name === student);
  };



